// Gerard's code

let times = new Array();

function longestZero(str) {
  const start = window.performance.now();
  let longZeros = '';
  let zeros = '';
  for (let i = 0; i < str.length; i++) {
    if (str[i] === '0') {
      zeros += '0';
    } else {
      if (longZeros.length < zeros.length) {
        longZeros = zeros;
      }
      zeros = '';
    }
  }
  const end = window.performance.now();
  times.push(end - start);
  return longZeros;
}

longestZero('0010000101010010001011000000111100000011100100001010000');

longestZero(
  '',
);

longestZero(
  '',
);

console.log(
  'gerard',
  times.reduce((total, num) => {
    return num + total;
  }, 0) / times.length,
);
